do local TweenService=game:GetService("TweenService");local UIS=game:GetService("UserInputService");local Players=game:GetService("Players");local Lighting=game:GetService("Lighting");local RunService=game:GetService("RunService");local player=Players.LocalPlayer;local camera=workspace.CurrentCamera;local playerGui=player:WaitForChild("PlayerGui");local blurSplash,blurMenu;local function createBlur(parent,size) local blur=Instance.new("BlurEffect",parent);blur.Size=size;return blur;end local splashGui=Instance.new("ScreenGui");splashGui.Name="SplashGui";splashGui.ResetOnSpawn=false;splashGui.IgnoreGuiInset=true;splashGui.Parent=playerGui;blurSplash=createBlur(Lighting,15);local splashFrame=Instance.new("Frame");splashFrame.Size=UDim2.new(0.3,0,0.12,0);splashFrame.Position=UDim2.new(0.5,0,0.5,0);splashFrame.AnchorPoint=Vector2.new(0.5,0.5);splashFrame.BackgroundTransparency=1;splashFrame.BackgroundColor3=Color3.new(0,0,0);splashFrame.BorderSizePixel=0;splashFrame.Parent=splashGui;local splashText=Instance.new("TextLabel");splashText.Size=UDim2.new(1,0,0.5,0);splashText.Position=UDim2.new(0,0,0,0);splashText.BackgroundTransparency=1;splashText.Text="made by shawzlx";splashText.TextColor3=Color3.new(1,1,1);splashText.Font=Enum.Font.Gotham;splashText.TextScaled=true;splashText.TextStrokeTransparency=0.7;splashText.TextTransparency=1;splashText.Parent=splashFrame;splashText.TextYAlignment=Enum.TextYAlignment.Bottom;splashText.TextXAlignment=Enum.TextXAlignment.Center;local smallText=Instance.new("TextLabel");smallText.Size=UDim2.new(1,0,0.3,0);smallText.Position=UDim2.new(0,0,0.5,0);smallText.BackgroundTransparency=1;smallText.Text="kobain ananı sikeyim";smallText.TextColor3=Color3.new(1,1,1);smallText.Font=Enum.Font.Gotham;smallText.TextScaled=true;smallText.TextTransparency=1;smallText.Parent=splashFrame;smallText.TextXAlignment=Enum.TextXAlignment.Center;smallText.TextYAlignment=Enum.TextYAlignment.Top;TweenService:Create(splashText,TweenInfo.new(0.5),{TextTransparency=0}):Play();TweenService:Create(smallText,TweenInfo.new(0.5),{TextTransparency=0}):Play();TweenService:Create(blurSplash,TweenInfo.new(0.5),{Size=15}):Play();delay(1.5,function() local fadeOutText=TweenService:Create(splashText,TweenInfo.new(0.5),{TextTransparency=1});local fadeOutSmall=TweenService:Create(smallText,TweenInfo.new(0.5),{TextTransparency=1});local fadeOutBlur=TweenService:Create(blurSplash,TweenInfo.new(0.5),{Size=0});fadeOutText:Play();fadeOutSmall:Play();fadeOutBlur:Play();fadeOutBlur.Completed:Wait();blurSplash:Destroy();splashGui:Destroy();end);local guiVisible=false;local espBoxesActive=false;local espNamesActive=false;local espBoxes={};local toggleKey=Enum.KeyCode.G;local function toggleBlur(state) if state then if  not blurMenu then blurMenu=createBlur(Lighting,24);end elseif blurMenu then blurMenu:Destroy();blurMenu=nil;end end local screenGui=Instance.new("ScreenGui");screenGui.Name="ShawzlxGUI";screenGui.ResetOnSpawn=false;screenGui.IgnoreGuiInset=true;screenGui.Parent=playerGui;screenGui.Enabled=false;local panel=Instance.new("Frame");panel.Size=UDim2.new(0.35,0,0.5,0);panel.Position=UDim2.new(0.32,0,0.25,0);panel.BackgroundColor3=Color3.fromRGB(20,20,20);panel.BackgroundTransparency=0.15;panel.BorderSizePixel=0;panel.Parent=screenGui;local corner=Instance.new("UICorner",panel);corner.CornerRadius=UDim.new(0,15);local stroke=Instance.new("UIStroke",panel);stroke.Color=Color3.fromRGB(90,90,90);stroke.Thickness=2;stroke.Transparency=0.6;local dragging=false;local dragInput,mousePos,framePos;panel.InputBegan:Connect(function(input) if (input.UserInputType==Enum.UserInputType.MouseButton1) then dragging=true;mousePos=input.Position;framePos=panel.Position;input.Changed:Connect(function() if (input.UserInputState==Enum.UserInputState.End) then dragging=false;end end);end end);panel.InputChanged:Connect(function(input) if (input.UserInputType==Enum.UserInputType.MouseMovement) then dragInput=input;end end);UIS.InputChanged:Connect(function(input) if ((input==dragInput) and dragging) then local delta=input.Position-mousePos ;panel.Position=UDim2.new(math.clamp(framePos.X.Scale + (delta.X/workspace.CurrentCamera.ViewportSize.X) ,0,1 -panel.Size.X.Scale ),framePos.X.Offset + delta.X ,math.clamp(framePos.Y.Scale + (delta.Y/workspace.CurrentCamera.ViewportSize.Y) ,0,1 -panel.Size.Y.Scale ),framePos.Y.Offset + delta.Y );end end);local title=Instance.new("TextLabel",panel);title.Size=UDim2.new(1,0,0.15,0);title.Position=UDim2.new(0,0,0,0);title.BackgroundTransparency=1;title.Text="shawzlx wallhack script";title.TextScaled=true;title.Font=Enum.Font.GothamBold;title.TextColor3=Color3.fromRGB(220,220,220);title.TextStrokeTransparency=0.7;local function makeButtonHoverEffect(button) local originalColor=button.BackgroundColor3;button.MouseEnter:Connect(function() TweenService:Create(button,TweenInfo.new(0.3),{BackgroundColor3=originalColor:Lerp(Color3.fromRGB(50,150,50),0.6)}):Play();end);button.MouseLeave:Connect(function() TweenService:Create(button,TweenInfo.new(0.3),{BackgroundColor3=originalColor}):Play();end);end local function createButton(text,posY,color) local btn=Instance.new("TextButton",panel);btn.Size=UDim2.new(0.8,0,0.12,0);btn.Position=UDim2.new(0.1,0,posY,0);btn.Text=text;btn.BackgroundColor3=color;btn.TextColor3=Color3.new(1,1,1);btn.TextScaled=true;btn.Font=Enum.Font.GothamSemibold;btn.AutoButtonColor=false;Instance.new("UICorner",btn).CornerRadius=UDim.new(0,12);makeButtonHoverEffect(btn);return btn;end local espBoxBtn=createButton("ESP Box Aç",0.22,Color3.fromRGB(0,180,70));local espNameBtn=createButton("ESP İsim Aç",0.36,Color3.fromRGB(0,140,200));local closeButton=createButton("Wallhack Kapat",0.5,Color3.fromRGB(180,40,40));local settingsTitle=Instance.new("TextLabel",panel);settingsTitle.Size=UDim2.new(1,0,0.12,0);settingsTitle.Position=UDim2.new(0,0,0.66,0);settingsTitle.BackgroundTransparency=1;settingsTitle.Text="Ayarlar";settingsTitle.TextScaled=true;settingsTitle.Font=Enum.Font.GothamBold;settingsTitle.TextColor3=Color3.fromRGB(220,220,220);settingsTitle.TextStrokeTransparency=0.7;local keyLabel=Instance.new("TextLabel",panel);keyLabel.Size=UDim2.new(0.55,0,0.15,0);keyLabel.Position=UDim2.new(0.05,0,0.78,0);keyLabel.BackgroundTransparency=1;keyLabel.Text="Menü Aç/Kapat Tuşu:";keyLabel.TextScaled=true;keyLabel.Font=Enum.Font.GothamSemibold;keyLabel.TextColor3=Color3.fromRGB(180,180,180);local keyBox=Instance.new("TextBox",panel);keyBox.Size=UDim2.new(0.35,0,0.15,0);keyBox.Position=UDim2.new(0.6,0,0.78,0);keyBox.ClearTextOnFocus=false;keyBox.Text=toggleKey.Name;keyBox.Font=Enum.Font.GothamBold;keyBox.TextScaled=true;keyBox.TextColor3=Color3.new(1,1,1);keyBox.BackgroundColor3=Color3.fromRGB(40,40,40);keyBox.CursorPosition= #keyBox.Text;keyBox.TextStrokeTransparency=0.8;keyBox.AutomaticSize=Enum.AutomaticSize.None;keyBox.TextWrapped=true;keyBox.ClearTextOnFocus=true;keyBox.TextEditable=true;keyBox.TextXAlignment=Enum.TextXAlignment.Center;keyBox.TextYAlignment=Enum.TextYAlignment.Center;Instance.new("UICorner",keyBox).CornerRadius=UDim.new(0,6);keyBox.FocusLost:Connect(function(enterPressed) if enterPressed then local text=keyBox.Text:upper();local found=false;for _,code in pairs(Enum.KeyCode:GetEnumItems()) do if (code.Name==text) then toggleKey=code;keyBox.Text=text;found=true;break;end end if  not found then keyBox.Text=toggleKey.Name;end end end);local function createESP(p) if (p==player) then return;end if ( not p.Character or  not p.Character:FindFirstChild("HumanoidRootPart")) then return;end if espBoxes[p] then return;end local box=Drawing.new("Square");box.Thickness=1.5;box.Color=Color3.fromRGB(0,255,0);box.Filled=false;box.Transparency=1;box.Visible=false;local name=Drawing.new("Text");name.Size=18;name.Color=Color3.new(1,1,1);name.Center=true;name.Outline=true;name.Text=p.Name;name.Visible=false;espBoxes[p]={Box=box,Name=name};end local function removeAllESP() for _,drawings in pairs(espBoxes) do if drawings.Box then drawings.Box:Remove();end if drawings.Name then drawings.Name:Remove();end end espBoxes={};end local function updateESP() for _,p in pairs(Players:GetPlayers()) do local esp=espBoxes[p];if (esp and p.Character and p.Character:FindFirstChild("HumanoidRootPart")) then local hrp=p.Character.HumanoidRootPart;local screenPos,visible=camera:WorldToViewportPoint(hrp.Position);if visible then local size=Vector2.new(50,100);if espBoxesActive then esp.Box.Size=size;esp.Box.Position=Vector2.new(screenPos.X-(size.X/2) ,screenPos.Y-(size.Y/2) );esp.Box.Visible=true;else esp.Box.Visible=false;end if espNamesActive then esp.Name.Position=Vector2.new(screenPos.X,(screenPos.Y-(size.Y/2)) -10 );esp.Name.Visible=true;else esp.Name.Visible=false;end else esp.Box.Visible=false;esp.Name.Visible=false;end end end end espBoxBtn.MouseButton1Click:Connect(function() espBoxesActive= not espBoxesActive;if espBoxesActive then espBoxBtn.Text="ESP Box Kapat";for _,p in pairs(Players:GetPlayers()) do createESP(p);end else espBoxBtn.Text="ESP Box Aç";end end);espNameBtn.MouseButton1Click:Connect(function() espNamesActive= not espNamesActive;if espNamesActive then espNameBtn.Text="ESP İsim Kapat";for _,p in pairs(Players:GetPlayers()) do createESP(p);end else espNameBtn.Text="ESP İsim Aç";end end);closeButton.MouseButton1Click:Connect(function() espBoxesActive=false;espNamesActive=false;espBoxBtn.Text="ESP Box Aç";espNameBtn.Text="ESP İsim Aç";removeAllESP();end);Players.PlayerAdded:Connect(function(p) p.CharacterAdded:Connect(function() wait(1);if (espBoxesActive or espNamesActive) then createESP(p);end end);end);RunService.RenderStepped:Connect(function() updateESP();end);UIS.InputBegan:Connect(function(input,gameProcessed) if gameProcessed then return;end if (input.KeyCode==toggleKey) then guiVisible= not guiVisible;screenGui.Enabled=guiVisible;toggleBlur(guiVisible);end end); end